    public function ToObject($Array) { 

        // Create new stdClass object 
        $object = new stdClass(); 

        // Use loop to convert array into 
        // stdClass object 
        foreach ($Array as $key => $value) { 
            if (is_array($value)) { 
                $value = ToObject($value); 
            } 
            
            $object->$key = $value; 
        } 
        
        return $object; 
        
    }     
    
